
public class LeftShiftOperatorEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//<< is used to shift all of the bits in a value to the left side a specified number of times
		System.out.println(10<<2);//10*2^2=>10*4
		
		System.out.println(10<<3);//10*2^3=>10*8

		System.out.println(10<<4);//10*2^4=>10*16
		
		System.out.println(15<<4);//15*2^4=>15*16
	}

}
