
public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte b=50;
		
		switch(b)
		{
		case 10:
			System.out.println("Hello Boss");
			
		case 20:
			System.out.println("Hello Boss I want Sapatu");
			
		case 50:
			System.out.println("Hello Boss I want Sapatu");
		}

	}

}
